from microbit import *import ds18b20import musicimport CCS811ds18b20.init(pin13)ccs811 = CCS811()#write your program:music.play(music.POWER_UP, pin=pin0, wait=True)display.scroll(str("Now Loading......"),wait=True,loop=False)while True:    value1 = pin1.read_analog()    light=0    pin14.write_analog(1)    print("Light")    print(light)    if (value1 <= 210):        pin2.write_digital(1)    else:        pin2.write_digital(0)    if (value1 <= 10 or value1 >= 750):        music.play(music.BA_DING, pin=pin0, wait=True)    #else:        #music.play('', pin=pin0, wait=True)    value2 = ds18b20.read()    CO2=""    TVOC=""    if(ccs811.check_data_ready()):        CO2 = ccs811.CO2_PPM()        TVOC = ccs811.TVOC_PPB()        print("Air Quality:"),        print("CO2:"+str(CO2)+" ppm")        print("TVOC:"+str(TVOC)+" ppb")    print("Temp:")    print(str(value2))    print("Shidu:")    print(str(value1))    print("--")    display.scroll(str("Temp:"+str(value2)+";Shidu:"+str(value1/10)+"%;"+"CO2:"+str(CO2)+"ppm;TVOC:"+str(TVOC)+"ppb"),wait=True,loop=False)    if (value2 >= 31):       music.play(music.DADADADUM, pin=pin0, wait=True)    if (value2 <= -3 and value2 != -127):       music.play(music.DADADADUM, pin=pin0, wait=True)    if (value2 > 35):       pin2.write_digital(1)       sleep(200)       pin2.write_digital(0)    sleep(200)